import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { BASE_URL } from '../utils/config';
import '../styles/Home.css'; // Dodajemo stilove za bolje oblikovanje

const Home = () => {
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTours = async () => {
      try {
        const response = await fetch(`${BASE_URL}/tours`);
        const data = await response.json();

        if (data.success) {
          setTours(data.data); // Pretpostavljamo da je 'data' u JSON odgovoru
          console.log(data.data);
        } else {
          console.error('Failed to fetch tours:', data.message);
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching tours:', error);
        setLoading(false);
      }
    };

    fetchTours();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="home-container">
      <h2>Our Tours</h2>
      <div className="tour-grid">
        {tours.length > 0 ? (
          tours.map((tour) => (
            <div className="tour-card" key={tour._id}>
              <img src={tour.image || '/path/to/default-image.jpg'} alt={tour.name} className="tour-image" />
              <h3>{tour.title || 'No name available'}</h3>
              {/* Provera da description nije undefined ili null */}
              <p>{tour.desc ? tour.description.substring(0, 100) + '...' : 'No description available'}</p>
              <Link to={`/tours/${tour._id}`} className="tour-link">
                Learn More
              </Link>
            </div>
          ))
        ) : (
          <p>No tours available at the moment.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
