import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';  // Importiranje AuthContext
import '../styles/Header.css';  // Tvoj CSS za header

const Header = () => {
  const { user, dispatch } = useContext(AuthContext);  // Dohvaƒáanje podataka iz AuthContext-a

  const handleLogout = () => {
    dispatch({ type: 'LOGOUT' });  // Pokreƒáemo logout akciju
    localStorage.removeItem('user');  // Brisanje korisniƒçkih podataka iz localStorage
  };

  return (
    <header className="header-container">
      <nav>
        <ul>
          <li>
            <Link to="/">Poƒçetna</Link>
          </li>
          {!user ? (
            <>
              <li>
                <Link to="/login">Prijava</Link>
              </li>
              <li>
                <Link to="/register">Registracija</Link>
              </li>
            </>
          ) : (
            <>
              <li>
                <span className="user-icon">
                  {user.username || "üë§"} {/* Prikazivanje korisniƒçkog imena ili ikone */}
                </span>
              </li>
              <li>
                <button onClick={handleLogout}>Odjava</button> {/* Dugme za odjavu */}
              </li>
            </>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
