import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { BASE_URL } from '../utils/config';
import '../styles/Home.css'; // Dodajemo stilove za bolje oblikovanje

const Home = () => {
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTours = async () => {
      try {
        const response = await fetch(`${BASE_URL}/tours`);
        const data = await response.json();
        setTours(data.data); // Pretpostavljamo da je 'data' u JSON odgovoru
        setLoading(false);
      } catch (error) {
        console.error('Error fetching tours:', error);
        setLoading(false);
      }
    };

    fetchTours();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="home-container">
      <h2>Our Tours</h2>
      <div className="tour-grid">
        {tours.map((tour) => (
          <div className="tour-card" key={tour._id}>
            <img src={tour.image} alt={tour.name} className="tour-image" />
            <h3>{tour.name}</h3>
            <p>{tour.description.substring(0, 100)}...</p>
            <Link to={`/tours/${tour._id}`} className="tour-link">
              Learn More
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
