import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { BASE_URL } from '../utils/config';
import { AuthContext } from '../context/AuthContext'; // Import AuthContext za provjeru prijave
import '../styles/TourDetails.css';

const TourDetails = () => {
  const { id } = useParams();
  const [tour, setTour] = useState(null);
  const { user } = useContext(AuthContext); // Dohvatimo korisnika iz AuthContext

  const [comment, setComment] = useState('');
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    const fetchTourDetails = async () => {
      try {
        const response = await fetch(`${BASE_URL}/tours/${id}`);
        const data = await response.json();
        setTour(data.data);

        // Dohvati recenzije povezane s ovom turom
        const reviewsResponse = await fetch(`${BASE_URL}/reviews/tour/${id}`);
        const reviewsData = await reviewsResponse.json();
        setReviews(reviewsData);
      } catch (error) {
        console.error('Error fetching tour details:', error);
      }
    };

    fetchTourDetails();
  }, [id]);

  const handleCommentChange = (e) => {
    setComment(e.target.value);
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();

    if (!comment.trim()) {
      alert('Molimo vas da napišete komentar.');
      return;
    }

    if (!user) {
      alert('Morate biti prijavljeni da biste ostavili komentar.');
      return;
    }

    try {
      const res = await fetch(`${BASE_URL}/reviews`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          productId: tour._id,
          username: user.username,  // Pretpostavljamo da user ima username
          reviewText: comment,
          rating: 5,  // Možete dodati rating ako želite
        }),
      });

      if (!res.ok) {
        const error = await res.json();
        alert(error.message);
      } else {
        const newReview = await res.json();
        setReviews([...reviews, newReview]);
        setComment(''); // Očisti input nakon uspješnog slanja
      }
    } catch (error) {
      console.error('Error posting comment:', error);
    }
  };

  if (!tour) {
    return <div>Loading...</div>;
  }

  return (
    <div className="tour-details-container">
      <h2>{tour.name}</h2>
      <img
        src={tour.photo || '/img/sarajevo.jpg'}
        alt={tour.title || 'No name available'}
        className="tour-image"
      />
      <p>{tour.description}</p>
      <p><strong>Cijena:</strong> {tour.price} KM</p>
      <p><strong>Velicina grupe:</strong> {tour.maxGroupSize}</p>
      <p><strong>Grad:</strong> {tour.city}</p>

      <div className="reviews-section">
        <h3>Recenzije</h3>
        {reviews.length === 0 ? (
          <p>Još uvijek nema recenzija za ovu turu.</p>
        ) : (
          reviews.map((review) => (
            <div key={review._id} className="review">
              <p><strong>{review.username}</strong></p>
              <p>{review.reviewText}</p>
              <p><strong>Ocjena:</strong> {review.rating}/5</p>
            </div>
          ))
        )}
      </div>

      <div className="comment-section">
        <h3>Ostavite komentar</h3>
        {user ? (
          <form onSubmit={handleCommentSubmit}>
            <textarea
              value={comment}
              onChange={handleCommentChange}
              placeholder="Napišite svoj komentar..."
              required
            ></textarea>
            <button type="submit">Pošaljite komentar</button>
          </form>
        ) : (
          <p>Morate biti prijavljeni da biste ostavili komentar.</p>
        )}
      </div>
    </div>
  );
};

export default TourDetails;
